import React, { useEffect } from 'react';
import Router from 'next/router';
import nextCookie from 'next-cookies';
import cookie from 'js-cookie';

const signin = ({ token }) => {
  cookie.set('token', token, { expires: 1 });
  Router.push('/goals');
};

const auth = ctx => {
  const { token } = nextCookie(ctx);

  /*
   * If `ctx.req` is available it means we are on the server.
   * Additionally if there's no token it means the user is not logged in.
   */
  if (ctx.req && !token) {
    ctx.res.writeHead(302, { Location: '/signin' });
    ctx.res.end();
  }

  // We already checked for server. This should only happen on client.
  if (!token) {
    Router.push('/signin');
  }

  return token;
};

const logout = () => {
  cookie.remove('token');
  // to support logging out from all windows
  window.localStorage.setItem('logout', Date.now());
  Router.push('/signin');
};

const withAuthSync = WrappedComponent => {
  const Wrapper = props => {
    const syncLogout = event => {
      if (event.key === 'logout') {
        Router.push('/signin');
      }
    };

    useEffect(() => {
      window.addEventListener('storage', syncLogout);

      return () => {
        window.removeEventListener('storage', syncLogout);
        window.localStorage.removeItem('logout');
      };
    }, [null]);

    return <WrappedComponent {...props} />;
  };

  Wrapper.getInitialProps = async ctx => {
    const token = auth(ctx);

    // eslint-disable-next-line max-len
    const componentProps = WrappedComponent.getInitialProps && (await WrappedComponent.getInitialProps(ctx));
    return { ...componentProps, token };
  };
  return Wrapper;
};

export {
  signin,
  auth,
  logout,
  withAuthSync,
};

import { runInAction } from 'mobx';
import Router from 'next/router';
import nextCookie from 'next-cookies';
import cookie from 'js-cookie';

import config from '../../config';

const axios = require('axios');

const setClassProps = (arr, self) => {
	arr.forEach(elem => {
		self[elem.name] = elem.value;
	});
};

const renderIf = (condition, content) => {
  if (condition) {
    return content;
  }
  return null;
};

const axiosCore = (verb, urlPart, data = null, serverToken = null) => {
	const token = serverToken || cookie.get('token');
	if (!token) Router.push('/signin');

	if (verb === 'get') {
		return axios[verb](
			`${config.API_URL}/${urlPart}`,
			token
			? {
				headers: {
					Authorization: `Bearer ${token}`,
				},
			}
			: {},
		);
	}
	return axios[verb](
		`${config.API_URL}/${urlPart}`,
		data,
		token
		? {
			headers: {
				Authorization: `Bearer ${token}`,
			},
		}
		: {},
	);
};

const getData = (urlPart, token = null) => axiosCore('get', urlPart, null, token);

const postData = (urlPart, data) => axiosCore('post', urlPart, data);

const patchData = (urlPart, data) => axiosCore('patch', urlPart, data);

const runInActionUtil = (self, prop, data) => {
	runInAction(() => {
		self[prop] = data;
	});
};

// eslint-disable-next-line consistent-return
const getInitialProps = async (ctx, urlPart) => {
	const { token } = nextCookie(ctx);
  const redirectOnError = () => {
		if (window !== 'undefined') {
			Router.push('/signin');
		}
		else {
			ctx.res.writeHead(302, {
				Location: '/signin',
			});
			ctx.res.end();
		}
	};

  try {
		const data = await getData(urlPart, token).then(res => res.data);
    if (data) {
      return data;
		}
		return await redirectOnError();
	}
	catch (error) {
    redirectOnError();
  }
};

export {
	setClassProps,
	renderIf,
	getData,
	postData,
	patchData,
	runInActionUtil,
	getInitialProps,
};

